import React from 'react';
import Slider from 'react-slick';
import './EventCard.css';

const EventCard = ({ images, title }) => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  return (
    <div className="event-card">
      <Slider {...settings}>
        {images.map((image, index) => (
          <img src={image} alt={`${title} ${index}`} key={index} className="event-image" />
        ))}
      </Slider>
      <h3>{title}</h3>
    </div>
  );
};

export default EventCard;
